import React, { Component } from 'react';
import firebase from 'firebase/app';

class GameManager extends Component {
    constructor(props) {
        super(props);
        this.state = {
            playerList: {}
        }
    }

    createRoom(organizer) {
        let roomId = Math.floor(10000000 + Math.random() * 90000000);
        let roomRef = firebase.database().ref('Rooms').child('Room' + roomId);

        roomRef.set({
            room_name: "Room" + roomId,
            organizer: organizer,
            player:null
        });
    }

    render() {
        let createRoom = this.createRoom("Marie");
        return(
            <div>
                {createRoom}
            </div>
        );
    }
}

export default GameManager;
