import React, { Component } from 'react';
import './App.css';
import GameManager from './GameManager';
import { Route, Switch, Redirect } from 'react-router-dom';
import firebase from "firebase/app";
let uniqid = require("uniqid");

class App extends Component{
    constructor(props) {
        super(props)
        this.state = {
            user: "",
            uid:""
        }
        this.updateUserType = this.updateUserType.bind(this);
    }

    updateUserType(type) {
        this.setState({user: type});
        this.handleCreateRoom();
    }

    handleCreateRoom() {
        let uid = uniqid();
        this.setState({uid: uid});
        let roomRef = firebase.database().ref("Rooms").push();
        roomRef.set({
            roomId: uid,
            room_name: "Room" + uid,
            user: this.state.user
        });

        console.log(roomRef);
    }
    render() {
    let Home = () => {
        return  (
            <div>
                <h1>Dive-In!</h1>
                <div className="buttonContainer">
                    <a href="/JoinRoom">
                        <button onClick={() => this.updateUserType("Player")}> Join a Room </button>
                    </a>
                    <a href="/NewRoom">
                        <button onClick={() => this.updateUserType("Organizer")}> New Room </button>
                    </a>
                </div>
            </div>
        );
    }

    return ( 
        <div className="App" >
            <header className="App-header" >
                <Switch>
                    <Route exact path="/" render={Home} />
                    <Route path="/NewRoom" component={(props) => <GameManager/>} />
                    <Redirect to='/' />
                </Switch>
            </header>
        </div>
    );
    }
}

export default App;
