import React, { Component } from "react";
import firebase from "firebase/app";
import {
  FormGroup,
  InputGroupAddon,
  InputGroupText,
  Input,
  Label
} from "reactstrap";
let uniqid = require("uniqid");

class GameManager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false
    };
  }

  generateRoomId() {
    return uniqid();
  }

  handleCreateRoom(organizer, category, settings) {
    let uid = this.generateRoomId();
    let roomRef = firebase
      .database()
      .ref("Rooms")
      .push();

    roomRef.set({
      roomId: uid,
      room_name: "Room" + uid,
      organizer: organizer,
      category: category,
      settings: settings
    });

    return uid;
  }

  handleJoin(player, id) {}

  createDropDownItems() {
      let items = [];
      for(let i = 0; i <= 10; i++) {
        items.push(<option key={i} value={i}>{i}</option>);
      }
      return items;
  }
  render() {
    return (
      <div>
        <FormGroup>
          <Label>Organizer Name </Label>
          <Input placeholder="First Name" name="name" id="name" />
          <Input type="select" name="select" id="exampleSelect">
            {this.createDropDownItems()}
          </Input>
        </FormGroup>
      </div>
    );
  }
}

export default GameManager;
