import React, { Component } from 'react';
import './App.css';
import GameManager from './GameManager';
import { Route, Switch, Redirect } from 'react-router-dom';
class App extends Component{
    constructor(props) {
        super(props)
        this.state = {
            user: "",
            roomId: ""
        }
    }

    handleUserType = (type) => {
        this.setState({user: type})
    }
    render() {
    let Home = () => {
        return  (
            <div>
                <h1>Dive-In!</h1>
                <div className="buttonContainer">
                    <a href="/">
                        <button onClick={this.handleUserType("Player")}> Join a Room </button>
                    </a>
                    <a href="/gameManager">
                        <button onClick={this.handleUserType("Organizer")}> New Room </button>
                    </a>
                </div>
            </div>
        );
    }

    return ( 
        <div className="App" >
            <header className="App-header" >
                <Switch>
                    <Route exact path="/" render={Home} />
                    <Route path="/gameManager" component={GameManager} />
                    <Redirect to='/' />
                </Switch>
            </header>
        </div>
    );
    }
}

export default App;
