import React, { Component } from "react";
import firebase from "firebase/app";
import {
    FormGroup,
    InputGroup,
    InputGroupAddon,
    Input,
    Label
} from "reactstrap";

class GameManager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fname: "",
      uid:"",
      roomName: "",
      numQuestions: 0,
      toggleAnalysis: false,
      toggleRanking: false,
      timer:0
    };
    this.handleOnChange = this.handleOnChange.bind(this);
  }

  componentDidMount() {
    let roomRef = firebase.database.ref("Rooms").push().name;
    this.setState({uid: roomRef, roomName: "Room-" + roomRef});
  }
  handleCreateRoom() {
    let roomRef = firebase
      .database()
      .ref("Rooms")
      .child(this.state.uid);

    roomRef.once('value', function (snapshot) {
      let snap = snapshot.val();
      if (snap) {
        roomRef.set({
          organizer: this.state.fname,
          uid: this.state.uid,
          room_name: this.state.roomName,
          category: this.state.category,
          settings: {
            Number_Questions: this.state.roomNamenumQuestions,
            category: this.state.category,
            timer: this.state.timer,
            showAnalysis: this.state.toggleAnalysis,
            showRanking: this.state.toggleRanking
          }
        });
      } else {
        alert("Error!");
      }
    });
    return this.props.uid;
  }

  handleOnChange = (e) =>{
    this.setState({
      [e.target.fname]: e.target.value,
      [e.target.roomName]: e.target.value,
      [e.target.numQuestion]: e.target.value,
      toggleAnalysis: !this.state.toggleAnalysis,
      toggleRanking: !this.state.toggleRanking,
      [e.target.timer]: e.target.value
    })
  }

  render() {
    return (
      <div className="Settings">
        <button onClick={this.io}></button>
        <FormGroup className="userName">
          <Label>Organizer Name </Label>
          <Input placeholder="First Name" name="fname" onChange={this.handleChange} id="fname" />
        </FormGroup>
        <FormGroup className="roomName">
          <Label>Custom Room Name</Label>
          <Input placeholder={this.state.roomName} name="roomName" onChange={this.handleChange} id="roomName" />
        </FormGroup>
        <div className="qNumber">
          <Label>Select number of Icebreaker Questions</Label>
          <InputGroup>
            <Input
              placeholder="Maximum is 15"
              name="numQuestion"
              min={0}
              max={15}
              type="number"
              step="1" 
              onChange={this.handleChange}
            />
            <InputGroupAddon addonType="append">Questions</InputGroupAddon>
          </InputGroup>
        </div>
        <FormGroup>
          <Label check>
            <Input type="checkbox" onChange={this.handleChange}/> Show Analysis
          </Label>
        </FormGroup>
        <FormGroup>
          <Label check>
            <Input type="checkbox" onChange={this.handleChange}/> Show Ranking
          </Label>
        </FormGroup>
        <Label>Discussion Time (optional)</Label>
        <InputGroup>
          <Input
            placeholder="Time"
            min={0}
            max={60}
            name="timer"
            type="number"
            step="1"
            onChange={this.handleChange}
          />
          <InputGroupAddon addonType="append">seconds</InputGroupAddon>
        </InputGroup>
      </div>
    );
  }
}

export default GameManager;
