import React, { Component } from 'react';
import firebase from "firebase/app";
import './css/room.css';
import {
    FormGroup,
    InputGroup,
    InputGroupAddon,
    Input,
    Label,
    Button
} from "reactstrap";
class JoinRoom extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fname: "",
            uid: ""
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleJoin = this.handleJoin.bind(this);
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    validateUid() {
        firebase.database().ref("Rooms").orderByChild("uid").equalTo(this.state.uid).once("value",snapshot => {
            if(snapshot.exists()) {
                let snap = snapshot.val();
                console.log(snap);
            }
        });
    }

    handleJoin() {
        if (this.validateUid) {
            let roomRef = firebase.database().ref("Rooms").child(this.state.uid).push("Players");

            roomRef.set({
                firstName: this.state.fname,
                answers: null
            })
        } else {
            alert("Sorry! Wrong Room Name!");
        }
    }
    render() {
        const isEnabled = (this.state.uid !== "" && this.state.fname !== "")
        return (
            <div>
                <h1 className="header"> Create Profile </h1>
                <Label>Name</Label>
                <Input placeholder="First Name" name="fname" onChange={this.handleChange} id="fname" />
                <Label>Room #</Label>
                <Input placeholder="Room Number" name="uid" onChange={this.handleChange} id="uid" />
                <Button onClick={this.handleJoin} disabled={!isEnabled}>Join</Button>
            </div>
        );
    }
}

export default JoinRoom;
