import React, { Component } from "react";
import firebase from "firebase/app";
import {
    FormGroup,
    InputGroup,
    InputGroupAddon,
    Input,
    Label
} from "reactstrap";
let uniqid = require("uniqid");

class GameManager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      organizerName: "",
      numQuestions: 0,
      toggleAnalysis: false,
      toggleRanking: false,
      timer:0
    };

  }

  generateRoomId() {
    return uniqid();
  }

  handleCreateRoom(organizer, category, settings) {
    let uid = this.generateRoomId();
    let roomRef = firebase
      .database()
      .ref("Rooms")
      .push();

    roomRef.set({
      roomId: uid,
      room_name: "Room" + uid,
      organizer: organizer,
      category: category,
      settings: settings
    });

    return uid;
  }

  handleJoin(player, id) {}

  createDropDownItems() {
    let items = [];
    for (let i = 0; i <= 15; i++) {
      items.push(
        <option key={i} value={i}>
          {i}
        </option>
      );
    }

    return items;
  }
  render() {
    return (
      <div className="Settings">
        <FormGroup className="name">
          <Label>Organizer Name </Label>
          <Input placeholder="First Name" name="name" id="name" />
        </FormGroup>
        <div className="">
          <Label>Select number of Icebreaker Questions</Label>
          <InputGroup>
            <Input
              placeholder="Maximum is 15"
              min={0}
              max={15}
              type="number"
              step="1"
            />
            <InputGroupAddon addonType="append">Questions</InputGroupAddon>
          </InputGroup>
        </div>
        <FormGroup>
          <Label check>
            <Input type="checkbox" /> Show Analysis
          </Label>
        </FormGroup>
        <FormGroup>
          <Label check>
            <Input type="checkbox" /> Show Ranking
          </Label>
        </FormGroup>
        <Label>Discussion Time (optional)</Label>
        <InputGroup>
          <Input
            placeholder="Time"
            min={0}
            max={60}
            type="number"
            step="1"
          />
          <InputGroupAddon addonType="append">seconds</InputGroupAddon>
        </InputGroup>
      </div>
    );
  }
}

export default GameManager;
