import React from 'react';
import './App.css';
import GameManager from './GameManager';
import { Route, Switch, Redirect } from 'react-router-dom';
class App {
    constructor() {
        this.state = {
            user: "",
            roomId: ""
        }
    }

    handleCreateRoom() {
        let uid = uniqid();
        let roomRef = firebase.database().ref('Rooms').push();

        roomRef.set({
            roomId: uid
        });

        this.setState({user: "Organizer", roomId:uid})
    }
    render() {
    let Home = () => {
        return  (
            <div>
                <h1>Dive-In!</h1>
                <div className="buttonContainer">
                    <a href="/">
                        <button> Join a Room </button>
                    </a>
                    <a href="/gameManager">
                        <button onClick={this.setState({})}> New Room </button>
                    </a>
                </div>
            </div>
        );
    }

    return ( 
        <div className="App" >
            <header className="App-header" >
                <Switch>
                    <Route exact path="/" render={Home} />
                    <Route path="/gameManager" component={GameManager} />
                    <Redirect to='/' />
                </Switch>
            </header>
        </div>
    );
    }
}

export default App;
