import React, { Component } from "react";
import firebase from "firebase/app";
import "./css/room.css";
import { Input, Label, Button, Row, Col } from "reactstrap";
class JoinRoom extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fname: "",
      uid: ""
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  validateUid() {
    firebase.database().ref("Rooms").orderByChild("uid").equalTo(this.state.uid)
  }

  handleJoin() {

  }
  render() {
    const isEnabled = this.state.uid !== "" && this.state.fname !== "";
    return (
      <div>
        <h1 className="header"> Create Profile </h1>
        <Label>Name</Label>
        <Input
          placeholder="First Name"
          name="fname"
          onChange={this.handleChange}
          id="fname"
        />
        <Label>Room #</Label>
        <Input
          placeholder="Room Number"
          name="uid"
          onChange={this.handleChange}
          id="uid"
        />
        <Label>Select Profile Image</Label>
        <Row>
            <Col>
                <img width="200px" alt="big penguin" src="/icons/profile1.png"></img>
            </Col>
        </Row>
        <Button onClick={this.handleJoin} disabled={!isEnabled}>
          Join
        </Button>
      </div>
    );
  }
}

export default JoinRoom;
