import React, { Component } from "react";
import firebase from "firebase/app";
import {
    FormGroup,
    InputGroup,
    InputGroupAddon,
    Input,
    Label
} from "reactstrap";

class GameManager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      organizerName: "",
      numQuestions: 0,
      toggleAnalysis: false,
      toggleRanking: false,
      timer:0
    };
    this.handleOnChange = this.handleOnChange.bind(this);
  }

  handleCreateRoom() {
    let roomRef = firebase
      .database()
      .ref("Rooms")
      .child(this.props.uid);

    roomRef.once('value', function (snapshot) {
      let snap = snapshot.val();
      if (snap) {
        roomRef.set({
          organizer: organizer,
          room_name: roomName,
          Number_Questions: numQuestions,
          category: category,
          settings: settings
        });
      } else {
        alert("Error!");
      }
    });
    return uid;
  }

  handleOnChange = (e) =>{
    this.setState({
      [e.target.name]: e.target.value,
      [e.target.roomName]: e.target.value,
      [e.target.numQuestion]: e.target.value,
      toggleAnalysis: !this.state.toggleAnalysis,
      toggleRanking: !this.state.toggleRanking,
      [e.target.message]: e.target.value
    })
  }
  render() {
    return (
      <div className="Settings">
        <FormGroup className="name">
          <Label>Organizer Name </Label>
          <Input placeholder="First Name" name="fname" onChange={this.handleChange} id="fname" />
        </FormGroup>
        <FormGroup className="name">
          <Label>Custom Room Name</Label>
          <Input placeholder={this.props.roomId} name="roomName" onChange={this.handleChange} id="roomName" />
        </FormGroup>
        <div className="">
          <Label>Select number of Icebreaker Questions</Label>
          <InputGroup>
            <Input
              placeholder="Maximum is 15"
              name="numQuestion"
              min={0}
              max={15}
              type="number"
              step="1" 
              onChange={this.handleChange}
            />
            <InputGroupAddon addonType="append">Questions</InputGroupAddon>
          </InputGroup>
        </div>
        <FormGroup>
          <Label check>
            <Input type="checkbox" onChange={this.handleChange}/> Show Analysis
          </Label>
        </FormGroup>
        <FormGroup>
          <Label check>
            <Input type="checkbox" /> Show Ranking
          </Label>
        </FormGroup>
        <Label>Discussion Time (optional)</Label>
        <InputGroup>
          <Input
            placeholder="Time"
            min={0}
            max={60}
            type="number"
            step="1"
          />
          <InputGroupAddon addonType="append">seconds</InputGroupAddon>
        </InputGroup>
      </div>
    );
  }
}

export default GameManager;
