import React, { Component } from "react";
import firebase from "firebase/app";
import {
    FormGroup,
    InputGroup,
    InputGroupAddon,
    Input,
    Label
} from "reactstrap";

class GameManager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      organizerName: "",
      numQuestions: 0,
      toggleAnalysis: false,
      toggleRanking: false,
      timer:0
    };
    this.handleCreateRoom = this.handleCreateRoom.bind(this);
  }

  handleCreateRoom(organizer, category, settings) {
    let uid = this.generateRoomId();
    let roomRef = firebase
      .database()
      .ref("Rooms")
      .child(this.props.uid);

    roomRef.once('value', function (snapshot) {
      let snap = snapshot.val();
      if (snap) {
        let keys = Object.keys(snap);
        if (!keys.includes(movie.name)) {
          basketRef.child(movie.name).push(newMovie);
        }
      } else {
        alert("Error!");
      }
    });
    roomRef.set({
      organizer: organizer,
      category: category,
      settings: settings
    });

    return uid;
  }

  handleJoin(player, id) {}

  render() {
    return (
      <div className="Settings">
        <FormGroup className="name">
          <Label>Organizer Name </Label>
          <Input placeholder="First Name" name="fname" id="fname" />
        </FormGroup>
        <FormGroup className="name">
          <Label>Custom Room Name</Label>
          <Input placeholder={this.props.roomId} name="roomName" id="roomName" />
        </FormGroup>
        <div className="">
          <Label>Select number of Icebreaker Questions</Label>
          <InputGroup>
            <Input
              placeholder="Maximum is 15"
              min={0}
              max={15}
              type="number"
              step="1"
            />
            <InputGroupAddon addonType="append">Questions</InputGroupAddon>
          </InputGroup>
        </div>
        <FormGroup>
          <Label check>
            <Input type="checkbox" /> Show Analysis
          </Label>
        </FormGroup>
        <FormGroup>
          <Label check>
            <Input type="checkbox" /> Show Ranking
          </Label>
        </FormGroup>
        <Label>Discussion Time (optional)</Label>
        <InputGroup>
          <Input
            placeholder="Time"
            min={0}
            max={60}
            type="number"
            step="1"
          />
          <InputGroupAddon addonType="append">seconds</InputGroupAddon>
        </InputGroup>
      </div>
    );
  }
}

export default GameManager;
