import React, { Component } from "react";
import firebase from "firebase/app";
import {
    Form,
    FormGroup,
    Input,
    Label
} from "reactstrap";
let uniqid = require("uniqid");

class GameManager extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false
    };
  }

  generateRoomId() {
    return uniqid();
  }

  handleCreateRoom(organizer, category, settings) {
    let uid = this.generateRoomId();
    let roomRef = firebase
      .database()
      .ref("Rooms")
      .push();

    roomRef.set({
      roomId: uid,
      room_name: "Room" + uid,
      organizer: organizer,
      category: category,
      settings: settings
    });

    return uid;
  }

  handleJoin(player, id) {}

  createDropDownItems() {
    let items = [];
    for (let i = 0; i <= 15; i++) {
      items.push(
        <option key={i} value={i}>
          {i}
        </option>
      );
    }

    return items;
  }
  render() {
    return (
      <div>
        <Form>
          <FormGroup>
            <Label>Organizer Name </Label>
            <Input placeholder="First Name" name="name" id="name" />
          </FormGroup>
          <FormGroup>
            <Label>Select number of Icebreaker Questions (1 to 15)</Label>
            <Input type="select" name="select" id="exampleSelect">
              {this.createDropDownItems()}
            </Input>
          </FormGroup>
          <FormGroup>
            <Label check>
              <Input type="checkbox" /> Show Analysis
            </Label>
          </FormGroup>
          <FormGroup>
            <Label check>
              <Input type="checkbox" /> Show Ranking
            </Label>
          </FormGroup>
        </Form>
      </div>
    );
  }
}

export default GameManager;
