import React, { Component } from 'react';
import firebase from "firebase/app";
import './css/room.css';
import {
    FormGroup,
    InputGroup,
    InputGroupAddon,
    Input,
    Label,
    Button
} from "reactstrap";
class JoinRoom extends Component {

    constructor(props) {
        super(props);
        this.state = {
            fname: "",
            uid: ""
        }
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    }
    render() {
        const isEnabled = (!this.state.uid === "" || !this.state.fname === "")
        return (
            <div>
                <h1 className="header"> Create Profile </h1>
                <Label>Name</Label>
                <Input placeholder="First Name" name="fname" onChange={this.handleChange} id="fname" />
                <Label>Room #</Label>
                <Input placeholder="Room Number" name="uid" onChange={this.handleChange} id="uid" />
                <Button onClick={this.handleCreateRoom} disabled={!isEnabled}></Button>
            </div>
        );
    }
}

export default JoinRoom;
